mapper.select:
SELECT id, email
FROM users
WHERE id = #{userId}

SELECT id, email, password, created_at, last_login, is_active
FROM users
WHERE email = #{email}

SELECT *
FROM users
WHERE provider = #{provider}
AND provider_id = #{providerId}

mapper.update:
UPDATE users
SET last_login = NOW()
WHERE provider = #{provider} AND provider_id = #{providerId}

UPDATE users
SET password = #{newPassword}
WHERE email = #{email}

mapper.select:
SELECT COUNT(*) > 0
FROM users
WHERE email = #{email}
AND phone_number = #{phoneNumber}
